<script>

export default {
  name: 'PolygonTheme',
  data() {
    return {
      deg: 5
    }
  },
  mounted() {
    document.addEventListener('mousemove', this.handleMousemove)
  },
  beforeDestroy() {
    document.removeEventListener('mousemove', this.handleMousemove)
  },
  methods: {
    handleMousemove(event) {
      const width = window.innerWidth

      const diff = event.clientX / (width / 100)
      this.deg = 0 + (diff * 0.1)
    }
  }
}
</script>

